import Car from "./Car";
class App extends React.Component {
  render() {
    const carditail = {
      color: "yellow",
      model: "mostang",
      year: "2014",
    };
    return (
      <React.Fragment>
        <h1>Wellcome to my website</h1>
        <p>
          my name is {props.name} and my fav color is {props.color}
        </p>
        <Car color={carditail} />
      </React.Fragment>
    );
  }
}

export default App;
