import React from "react";


//Just like HTML DOM events, React can perform actions based on user events.
    const shoot = () => {
        alert("Great Shot!");
      }
class Event extends React.Component{

    render(){
        return(
            <button onClick={shoot}>Take the Shot!</button>
        )
    };
};

export default Event ;
