import React from "react";

class Temperature extends React.Component{
    constructor(props){
        super(props);
        this.state({
            celsius: 30
        })

    }
    render(){
        return(
            <div>
                <h1>Temperature</h1>
                <label>Celsius:</label>
                <input type="text" value={this.state.celsius} />
            </div>
        )
    }
}

export default Temperature;
